provider.selectSQL:
DECLARE @date1 SMALLDATETIME; DECLARE @sheet1 INT;
IF NOT EXISTS ( SELECT * FROM otherRefuellingSheets WHERE fuelId = {_fuel} AND groupAccId = {_group} AND sheetState = 0) BEGIN
SET @date1 = CONVERT(SMALLDATETIME, dbo.getParameterByName('REFUELLING_DATE'), 104);
INSERT INTO otherRefuellingSheets (fuelId, groupAccId, refuellingDate) VALUES ( {_fuel}, {_group}, @date1);
END
SELECT TOP 1 @sheet1 = s.oSheetNumber, @date1 = s.refuellingDate FROM otherRefuellingSheets s WHERE s.fuelId = 0{_fuel} AND groupAccId = {_group} AND sheetState = 0 ORDER BY oSheetNumber DESC;
SELECT refuellingDate = @date1, personId = 0, string = '', department = ' ', oSheetNumber = @sheet1, quantity = 0
