procedures.procedure:
IF (0{quantity} > 0) BEGIN
DECLARE @fuelId INT; SELECT @fuelId = fuelId FROM waybillsRefuelling WHERE recordId =0{_recordId};
declare @owner int set @owner = dbo.getCurrentOwner()
INSERT INTO waybillsRefuelling ( refuellingDate, ownerId, waybillNumber, garageNumber, sheetNumber, fuelId, refuellingId, driverId, quantity, correctionId) VALUES( convert(smalldatetime,'{refuellingDate}',104), @owner, 0{_waybill}, 0{_garnom}, {sheetNumber}, @fuelId, 0, {driverId}, {quantity}, {_recordId});
UPDATE waybillsRefuelling SET quantity = 0 WHERE recordId = 0{_recordId}
END
